_custom.scss:
/* Add all site style customizations in this file */
$screen-xs: 400px; // mobile landscape
$screen-sm: 568px; // tablet portrate
$screen-md: 770px; // tablet landscape
$screen-lg: 1020px; // Desktop
$screen-xl: 1200px; // Large Desktop & HD TV

@import 'simplebar/simplebar';

@mixin respond-at($media) {
    @media only screen and (max-width: $media){ @content; }
}

@mixin placeholder {
  &::-webkit-input-placeholder {@content}
  &:-moz-placeholder           {@content}
  &::-moz-placeholder          {@content}
  &:-ms-input-placeholder      {@content}
}

/*Colors*/
$color-background-pdp: stencilColor('product-details-bg');

/*
$futura-font:'Futura', sans-serif;
*/

.site-branding {
    margin: 10px 0 0;
    .site-branding-link {
        img {
            min-width: 250px;
        }
    }

    &.has-logo .site-branding-link {
        position: relative;
        height: 0;
        min-width: 250px;
        padding-top: 20%;

        img, svg {
          position: absolute;
          top: 0;
          left: 0;
          height: 100%;
          width: 100%;
          min-width: none;
        }
    }
}
.site-branding-centered { margin: 10px 0 0; }

/*Gen Styles*/
.section {
    padding: 3rem 0;
}

.mega-nav.dropdown-open {
    > a {
        height: auto;
        min-height: 3.5rem;
        padding-bottom: 1rem;
    }
}

.mega-nav.hover-dropdown:hover { @extend .mega-nav.dropdown-open; }

.mega-nav.hover-dropdown .dropdown-panel {
    display: block;
    left: -1000vw;
    opacity: 0;
    padding: 1.5rem 0 0;
}

.mega-nav.hover-dropdown:hover .dropdown-panel {
    @extend .dropdown-panel.visible;
    left: 0;
    transition: left 0s, opacity 0.25s ease;
    transition-delay: 0.24s, 0.25s;
}

.mega-nav-panel {
    border-top: 1px solid #ccc;
    box-shadow: 0 3px 3px rgba(0, 0, 0, .20);

    &, .compressed & { top: 3rem; }
}

/* Mega Nav - Common Styles (Columns or Flex) */
.mega-nav-list {
    text-align: left;
    // height: calc(100vh - 240px);
    max-height: 450px;
    margin-bottom: 1.5rem;

    &--less-content { max-height: 300px; }
    &--even-less-content { max-height: 200px; }

    @media screen and (-ms-high-contrast: none) {
        height: 450px;

        &--less-content { height: 300px; }
        &--even-less-content { height: 200px; }
    }

    a {
        line-height: 1.15em;
        margin-bottom: 0;
        margin-left: 0.5rem;
        text-indent: -0.5rem;
        color: $color-link;

        &.highlight-red { color: #c0392b; }
        &.shop-all { font-style: italic; }
    }

    .parent {
        //max-width: 25%;
        margin-bottom: 2rem;
        padding: 0 1.5rem 0 0;
        white-space: normal;

        &--collapse {
            margin-bottom: 0.5rem;
        }

        > a {
            font-size: rem-calc(16);
            font-weight: bold;
        }

        // Necessary to space out categories with children, but collapse adjacent flat categories
        // &.has-children {
        //     &:before, &:after {
        //         content: '';
        //         display: block;
        //         float: left;
        //         clear: both;
        //         height: 1.5rem;
        //     }

        //     > a { clear: both; }
        // }

        ul.children li:nth-child(10) {
            &, & ~ li { display: none; }
        }
    }

    .children a {
        opacity: 1;
        color: lighten($color-link, 25%);
    }
}



// Temporary for static menu
.mega-nav-list--shop-by-brand > li {
    margin-bottom: 0.5rem;
    padding: 0 1.5rem 0 0;
    white-space: normal;
    > a {
        font-size: rem-calc(16);
        font-weight: bold;
    }
}
.mobileNav__menuTitle {
    text-transform: uppercase;
    color: #999;
    font-size: 1.2em;
}
.nav-mobile-item--seperator {
    margin-top: 5px;
    border-top: 1px solid #c0c2c9;
    padding-top: 5px;
}


/* Mega Nav - CSS Columns */
// .mega-nav-list {
//     display: block;
//     column-count: 4;
//     column-fill: balance;
// }

/* Mega Nav - Flexbox */
.mega-nav-list {
    display: flex;
    justify-content: flex-start;
    flex-direction: column;
    flex-wrap: wrap;
    align-items: stretch;
    align-content: stretch;

    .parent {
        width: 25%;
    }
}

.mega-nav-landing-wrapper {
    display: flex;
    justify-content: center;

    .mega-nav-landing {
        flex: 0 1 33%;
        order: 10000;
        margin-bottom: 2rem;
        margin-right: 15px;

        + .mega-nav-landing { display: none; }

        &, a {
            display: block;
            position: relative;
        }

        a { cursor: pointer; }

        .landing__image {
            display: block;
            width: 100%;
        }

        .landing__caption {
            position: absolute;
            display: block;
            bottom: 0;
            right: 0;
            width: 100%;
            padding: 5px;
            background: rgba(255, 255, 255, 0.75);
            color: #000;
            text-align: center;

            &:after {
                content: ' >';
            }
        }
    }

    .mega-nav-landing-list-wrapper { flex: 1 0 auto; }

    .mega-nav-landing ~ .mega-nav-landing-list-wrapper .mega-nav-list {
        align-content: stretch;

        .parent { max-width: 35%; }
    }

}


// @media screen and (max-width: 1279px) {
//     .container {
//         max-width: 990px;
//     }
// }

.site-header {
    border: none;
    // box-shadow: 0 3px 4px rgba(0, 0, 0, .20);
}

.site-header.compressed {
    box-shadow: 0 3px 10px rgba(0, 0, 0, 0.09);
}

.site-branding a {
    display: block;
    padding: 0 10px;
}

.site-branding .site-branding-link img {
    min-width: unset;
}

/*AuthenTEAK Navigation*/
.top-bar {
    padding: 15px 0 0 0;
    transition: padding 0.25s ease;

    .compressed & { padding: 0; }
}

.top-bar .container {
    display: flex;
    justify-content: space-between;

    &::after { display: none; }
}

.top-bar .container::after,
.top-bar .product-tabs::after {
    border: none;
}

.top-bar-left, .top-bar-center, .top-bar-right {
    flex: 0 0 auto;
    display: inline-block;
    margin-right: -4px;
    vertical-align: top;
}

.top-bar-left {
    width: 30%;
    transition: width 0.25s ease;

    .compressed & { width: 27%; }
}

.top-bar-center {
    width: 40%;
}

// .top-bar-right {
//     width: 30%;
// }

.main-nav-bar .top-level-nav-link {
    padding: rem-calc(12 ($main-nav-spacing - 5));
    font-size: 0.82rem;
    font-size: calc(0.35vw + .40rem);
    letter-spacing: 0.05em;
}

/*Atmo-Center-Search*/
.atmo-center-search {
    max-width: none;
    margin: 1.4rem auto 0;
    padding: 0 17px;

    .form-input:not([type="radio"]):not([type="checkbox"]):not(.quantity-input) {
        box-shadow: 0 0 0 1px #e6e9ef inset;
        height: 2rem;
        padding: 0 1rem;
        @include placeholder() { color: $color-topbar-text; }
    }

    button.button-search-submit {
        color: #fff;
        background-color: #6ea438;
        padding: 7px;
        right: 17px;
        border-radius: 3px;
        border-top-left-radius: 0;
        border-bottom-left-radius: 0;
    }

    button.button-search-submit .icon-search {
        width: 18px;
        height: 18px;
    }
}

.button {
    background-color: #ec9d08;
    box-sizing: border-box;
    border: 2px solid #ec9d08;
}

.button:hover {
    background-color: #fff;
    color: #ec9d08;
    border: 2px solid #ec9d08;
}

.button.button-secondary {
    background-color: #fff;
    border: 2px solid #ccc;
    color: #878787;
    box-shadow: none;
}

.button.button-secondary:hover {
    background-color: #fff;
    border: 2px solid #ec9d08;
    color: #ec9d08;
}

.button.button-tertiary {
    background: #6ea438;
    border: 2px solid #6ea438;
    box-sizing: border-box;
    padding: 15px;
}

.button.button-tertiary:hover {
    box-shadow: none;
    border: 2px solid #6ea438;
    color: #6ea438;
}

.button-group-item.active {
    background-color: $color-border;
    border-color: $color-border;
}

.top-bar-button {
    height: auto;
    padding-top: 12px;
    padding-bottom: 6px;
}

.customer-links { font-weight: 400; }

.customer-links:after,
.customer-utils .button-wishlist:after,
.logo-align-center .customer-utils .button-wishlist:after {
    width: 0;
}

span.head-phone-number {
    display: inline-block;
    padding: 10px 10px 18px;
    margin-right: 20px;
}

.mini-cart-subtotal[data-cart-quantity="0"],
.mini-cart-subtotal.mini-cart-quantity-mobile[data-cart-quantity="0"] { 
    display: none; 
}

@media screen and (max-width: 1023px) {
    .mini-cart-subtotal {
        display: none;
    }

    .mini-cart-subtotal.mini-cart-quantity-mobile {
        // background: #6ea438;
        // border-radius: 0.8em;
        // -moz-border-radius: 0.8em;
        // -webkit-border-radius: 0.8em;
        // color: #fff;
        display: inline-block;
        margin: 0;
        // font-weight: bold;
        // line-height: 1.5em;
        // text-align: center;
        // width: 1.6em;
        // font-size: .8em;
        // position: absolute;
        // right: -15px;
        // top: -10px;
    }
}

.mini-cart-open {
    .site-canvas, .site-header {
        transform: none;
    }
}
.mini-cart {
    z-index: 1000;
}

.mini-cart-item-options .option-value {
    color: inherit;
}

.mini-cart-actions {
    display: flex;
    flex-direction: row;

    > * { flex: 1 0 auto; }
}

.mini-cart-actions .button-secondary:hover {
    box-shadow: none;
}

.mini-cart-item-options .option-value {
    display: block;
    margin-top: 10px;
}

.mini-cart-item-options .option-value-label-mc {
    display: block;
    font-size: .8em;
    line-height: .8em;
}

.mini-cart-item-options .option-value:not(:last-child):after {
    content: " ";
}

span.global-header-text-promo {
    display: block;
    color: #6ea438;

    a { color: inherit; }
}

.main-nav-bar > .nav-container-wide {
    max-width: 1600px;
}

.main-nav-bar > .container {
    &, .compressed & { height: 3rem; }
}

.nav-container-wide .navigation {
    width: 100%;
    margin-left: 0;
}

.main-nav-bar > .nav-container-wide .navigation ul li {
    margin-right: 0;
}

@media screen and (max-width: 1023px) {
    header.site-header.compressed ~ .site-canvas .navigation-mobile {
        margin-top: -30px;
    }
}

table.geotrust-ssl-seal-table {
    position: absolute;
    bottom: 20px;
}

.site-footer {
    font-weight: normal;
}

.site-footer .bottom-bar {
    padding: 25px 0;
}

@media screen and (max-width: 1023px) {
    .site-footer .footer-nav {
        width: 33.333%;
        margin-right: 0;
    }

    .site-footer .footer-info,
    .site-footer .footer-social {
        width: 50%;
        margin-right: 0;
        padding-right: 0;
    }
}

@media screen and (min-width: 1024px) {
    .logo-align-left .top-bar-center .site-branding, .logo-align-left .top-bar-center .site-branding-centered, .logo-align-right .top-bar-center .site-branding, .logo-align-center .top-bar-center .site-branding, .mobile-search-wrapper {
        display: none;
    }
}

@media screen and (max-width: 1024px) {
    .site-footer .footer-nav,
    .site-footer .footer-info,
    .site-footer .footer-social {
        width: 100%;
    }

    .site-footer .footer-info,
    .site-footer .footer-social {
        text-align: center;
        border-top: 1px solid #ccc;
    }

    .site-footer .footer-info {
        border: none;
    }

    .site-footer .footer-info {
        padding-bottom: 2em;
    }

    .site-footer .footer-social {
        padding-top: 1.5em;
    }

    .footer-nav .footer-title {
        border-bottom: 1px solid #ccc;
        padding-bottom: 10px;
        margin: 10px 0;
        cursor: pointer;
        user-select: none;
    }

    table.geotrust-ssl-seal-table {
        position: unset;
        margin: 0 auto;
    }

    .site-footer .bottom-bar {
        padding: 0;
    }
}

@media screen and (max-width: 1023px) {
    span.head-phone-number,
    span.shipping-message,
    .top-bar-left,
    .top-bar-center .atmo-center-search,
    .top-bar-right {
        display: none; 
    }

    .mobile-search-wrapper {
        position: absolute;
        width: 100%;
        top: 100%;
        left: 0;
        padding: 0 0 10px 0;
        background-color: #fff;

        .atmo-center-search {
            margin: 0 auto;

            .form-field {
                margin-bottom: 0;
            }
        }
    }

    .compressed .mobile-search-wrapper{
        box-shadow: 0 3px 5px rgba(0, 0, 0, 0.09);
    }

    .top-bar-center {
        display: block;
        width: 160px;
        margin: 0 auto;

        h1 {
            margin: 8px 0 0;
        }
    }


    .logo-align-left .top-bar-left .site-branding {
        display: none;
    }
}

.site-footer .footer-social a svg {
    width: 24px;
    color: #878787;
}

.site-footer .footer-social a:hover svg {
    color: #6ea438;
}

.site-footer {
    color: $color-footer-text;
}

footer.mobile-footer .footer-col.footer-accordion nav {
    display: none;
}

footer.mobile-footer .footer-col.footer-accordion .footer-title span::after {
    position: relative;
    content: "►";
    padding-left: 10px;
    color: #999;
    font-size: .75em;
    float: right;
}

footer.mobile-footer .footer-col.footer-accordion .footer-title.accordion-open span::after {
    position: relative;
    content: "▼";
}

.blog-link {
    margin: 25px 0;
}

.blog-link p {
    text-align: center;
    font-style: italic;
    margin: 0;
}

.blog-link a  {
    display: block;
}

.blog-items-list .post{
    max-width: 100%;
    width: 100%
}

.page-header {
    padding: 20px 8vw;
}

.blog-post-single .post-main {
    max-width: 100%;
    margin: 0 8.2vw;
}

/* Category Page Styles */
.form-field--nxt-pagination {
    width: 100%;
    text-align: center;

    @media screen and (min-width: 768px) {
        width: auto;
        text-align: right;
    }
}
.nxt-results__info {
    font-size: 0.8rem;
    line-height: 1.1em;
}
#product-results, .product-listing-wrapper, .product-listing--category-landing {
    .listing-description {
        border: none;
        margin: 50px auto;
        background-color: #f5f5f5;
        padding: 40px;

        > .subtext { display: none; }

        h2 {
            font-size: 45px;
            text-align: center;
            color: #5e5e5e;
            font-weight: 300;
            margin-top: 0px;
            text-transform: uppercase;
            word-wrap: break-word;
        }
    }
}

/* Category Landing Page Styles */
.product-listing--category-landing {
    .listing-header {
        display: block;

        .listing-title {
            float: none;
            margin: 0;
            text-align: center;
            font-size: 3rem;
            text-transform: uppercase;
            font-weight: 300;
            letter-spacing: 0.05em;
        }
    }
}
.category-landing-grid {
    display: flex;
    margin: 0 -10px;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: center;
    align-items: flex-start;

    &__item {
        display: flex;
        flex: 0 0 100%;
        width: 100%;
        padding: 10px 10px 30px;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        text-align: center;

        @media screen and (min-width: 480px) {
            flex: 0 0 50%;
            width: 50%;
        }

        @media screen and (min-width: 768px) {
            flex: 0 0 33%;
            width: 33%;
        }

        @media screen and (min-width: 1024px) {
            flex: 0 0 25%;
            width: 25%;
        }

        &--brand {
            padding: 10px;

            @media screen and (min-width: 480px) {
                padding: 10px 30px 30px;
            }
        }
    }

    &__link {
        flex: 0 0 auto;
        width: 100%;
    }

    &__image {
        position: relative;
        display: block;
        width: 100%;
        margin-bottom: 10px;
        background-size: cover;
        background-repeat: no-repeat;
        background-position: center center;

        &::after {
            content: '';
            display: block;
            padding-top: 66.7%;
        }

        &--brand {
            background-size: contain;
            max-width: 300px;
            margin: 0 auto 10px;

            &::after { padding-top: 50%; }
        }

        &--placeholder {
            background: #e6e9ef;
            box-shadow: inset 0 0 10vw #999999;
        }
    }

    &__title {
        flex: 0 0 auto;
        width: 100%;
        text-transform: uppercase;
        font-size: calc(0.33vw + 1rem);
        line-height: 1.1em;
        letter-spacing: 0.025em;
    }
}



// Category page styles
#product-results, .product-listing-wrapper--category {
    display: flex;
    flex-direction: column;

    @media screen and (min-width: 1024px) {
        flex-direction: row;
        flex-wrap: wrap;

        .listing-sidebar {
            flex: 0 1 25%;
        }

        > .Content {
            flex: 0 3 73%;
        }

        > .listing-description {
            flex: 0 4 100%;
        }
    }

    > .Content .product-listing-grid {
        float: none;
        width: 100%;

        &::after {
            content: '';
            display: block;
            clear: both;
        }
    }

    .custom-text-panel {
        font-size: 0.8rem;

        .custom-text {
            display: inline;
            * { display: none; }

            + .custom-text::before {
                content: '\25CF';
                margin: 0 0.25rem;
            }

            &--more-colors::after { content: 'More Options'; }
            &--quick-ship::after { content: 'Quick Ship'; }
            &--free-shipping::after { content: 'Free Shipping'; }
        }
    }
}

@media only screen and (max-width: $screen-sm) {
    .listing-wrapper{
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
        width: 100%;
        justify-content: flex-start;
        align-content: flex-start;
        align-items: flex-start;
        flex-wrap: wrap;
        flex: 1 0 auto;
        padding-top:20px;
    }

    .product-listing-grid{
        @extend .listing-wrapper;
    }

    .product-listing-grid .product-grid-item {
        flex: 1 0 47%;
        padding-right: 10px;
        align-self: flex-start;

        &:nth-of-type(2){
            padding-right: 0;
            padding-left: 10px;
        }
    }
}

.product-listing-grid,
.listing-header,
.listing-sidebar {
    .form-field {
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
        justify-content: center;
        align-items: center;
        margin: 1rem 0.5rem 0;

        .form-label { display: inline-block; }

        .form-select-wrapper {
            display: inline-block;
            vertical-align: middle;
            margin-left: 16px;
        }
    }
    .button-group { margin: 1rem 0.5rem 0; }
}

.listing-header {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: flex-end;
    margin-left: 0;
    margin-right: 0;
    text-align: left;

    &::before, &::after { display: none; }

    .form-field--nxt-rpp {
        display: none;

        @media screen and (min-width: 768px) { display: block; }
    }

    background-color: transparent;

    > .listing-title { flex: 1 0 auto; }
    > .listing-utils .sorting { margin-left: 0; }
}

.listing-sidebar {
    &__mobile-toggle, .nxt-narrow-search {
        width: 100%;
        padding: 0.5em;
        background: #FFF;
        border: 1px solid #e6e9ef;
        text-align: center;
        font-size: 1.2rem;
        text-transform: uppercase;
        outline: none;

        &:hover, &:focus, &:active {
            background: #e6e9ef;
            border-color: #ccc;
        }
    }

    &__mobile-collapse {
        display: none;

        &.is-open { display: block; }
    }

    @media screen and (min-width: 1024px) {
        &__mobile-toggle { display: none; }
        &__mobile-collapse { display: block !important; }
        .nxt-narrow-search, .form-field--nxt-rpp { display: none; }
    }

    .nxt-refinements-container {
        display: none;

        @media screen and (min-width: 1024px) { display: block !important; }
    }
}

.brand-header-wrapper {
    background-size: cover;
    background-position: center;

    h1, h2 {
        color: #fff;
        text-transform: uppercase;
        text-align: center;
        font-weight: 300;
        padding: 14% 0;
        font-size: 48px;
        background-color: rgba(26, 26, 26, 0.31);
    }

    &-lv h1 {display:none;}
}

section.product-listing {
    .product-listing-wrapper > .listing-header { width: 100%; }
    .Content > .listing-header > .listing-title,
    .Content > .TitleHeading { display: none; }

    @media screen and (min-width: 1024px) {
        .product-listing-wrapper > .listing-header--search { display: none; }
        .Content > .listing-header > .listing-title,
        .Content > .TitleHeading { display: block; }
    }
}

a.nxt-img--no-ratio {
    background-size: contain;
    background-position: center center;
    background-repeat: no-repeat;

    &::before {
        content: '';
        display: block;
        width: 100%;
        padding-bottom: 100%;
    }

    img {
        display: none;
    }
}

/*Product Details Page Styles*/
.product-details {
    background-color: $color-background-pdp; 
}

.product-details-column {
    padding: 0 20px;
}

.slick-list, .slick-slide a {
    // max-height: calc(100vw - 60px);
}

.slick-dots{
    bottom: -50px;
    top: auto;
    margin: 0;
}

@media only screen and (min-width: $screen-lg) {
    .slick-arrow, .slick-dots{
        display: none !important;
    }
