            display: flex;
            flex-flow: row wrap;
            flex: 1 0 auto;
        }
        .simplebar-track.horizontal { display: none; }

        .swatch-radio:checked + .swatch::after {
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-size: 90%;
            background-position: center center;
            background-repeat: no-repeat;
            transform: none;
