            @media screen {
                max-height: none;
            }
        }

        .top-bar-left {
            @media print, screen and (min-width: 768px) {
                display: table-cell;
                flex: 0 0 auto;
                width: 27%;

                .site-branding { display: block; }
            }

            @media screen and (max-width: 1024px) {
                .site-branding img { max-height: 40px; }
            }
        }

        .print-mode-controls {
            display: flex;
            flex-direction: row-reverse;
            align-self: flex-end;
            flex: 0 1 auto;
            align-items: center;
            margin: 0 1rem;
            font-size: 0.8rem;
            text-align: right;

            @media print { display: none; }

            &__actions {
                flex: 1 0 auto;
                margin-bottom: 0.5rem;
            }

            &__sections {
                display: flex;
                flex-direction: row;
                flex-wrap: wrap;

