                background-color: $color-button-background;
                padding: 0.5rem 1rem;

                &:hover { background-color: #FFF; }
            }
        }
    }

    .site-canvas { padding-top: 100px !important; }

    .slick-list {
        height: auto !important;

        .slick-track {
            max-width: 100%;
            transform: none !important;

            .slick-slide {
                display: none;

                &.slick-active {
                    display: block;
                    max-width: 100%;

                    a.product-image {
                        height: auto !important;

                        &::before { display: none; }

                        img {
                            position: relative;
                            height: auto;

                            opacity: 1 !important;
                        }
                    }
                }
            }
        }
    }

    .mobile-share-consult {
      display: block;
      text-align: center;
    }

    .print-mode-swatches {
        display: block;
        width: 1230px;
        max-width: 100%;
        margin: 0px auto;

        .form-field-title {
            border-bottom: none;
            text-align: left;
            font-size: 1.5rem;
            line-height: 1.1em;
            padding: 10px 20px 5px;
            color: #000;

            &::before { display: none; }

            .swatch-value { display: none; }

            .print-mode-option-control {
                margin-left: 0.5rem;
                font-size: 0.8rem;
                font-weight: normal;
                text-transform: none;
                color: #000;
            }
        }

        .form-field-swatch {
            .form-field-control-wrapper {
                width: 100%;
                max-height: none;
                max-width: none;
                padding: 0 20px;
                box-shadow: none;
