            &.is-print-option-hidden {
                .form-field-title {
                    color: rgba(0,0,0,0.4);

                    @media print { display: none; }
                }
                .form-field-control-wrapper { display: none; }
            }
        }

        .form-field-swatch__request-button { display: none; }
    }

    .mobile-tab-wrapper {
        display: block;
        width: 1230px;
        max-width: 100%;
        margin: 0px auto;

        a[data-tabval="descTab"], #descTab,
        a[data-tabval="specsTab"], #specsTab,
