components/common/customizations/navigat:
{{!
  @param nav_items > either "pages" or "categories", passed from parent template
  @param parent > "parent" category
}}

<div class="dropdown-panel mega-nav-panel">
	<div class="container">
		<div class="mega-nav-landing-wrapper" id="category_{{parentId}}">

			<div class="mega-nav-landing"></div><!-- desktop nav image -->

			<div class="mega-nav-landing-list-wrapper">
				<ul class="mega-nav-list {{#if theme_settings.wrap-meganav}}wrap-cols{{/if}}">
					{{#each nav_items}}
					{{#startsWith "Shop by Collection" name}}{{ else }}
					<li class="parent {{#if children}}has-children{{else}}parent--collapse{{/if}} tier-dropdown">
						<a itemprop="url" href="{{url}}" title="{{name}}" {{#if image}}data-category-image="{{getImage image "large"}}"
							{{/if}}><span itemprop="name">{{name}}</span></a>

						{{#if children}}
						<ul>
							{{#each children}}
							{{#startsWith "Shop All Brands" name}}{{ else }}
							<li>
								<a itemprop="url" title="{{name}}" href="{{url}}" {{#if image}}data-category-image="{{getImage image "large"}}" {{/if}}>
									<span itemprop="name">{{name}}</span>
								</a>
							</li>
							{{/startsWith}}
							{{/each}}
						</ul>
						{{/if}}

					</li>
					{{/startsWith}}
					{{/each}}
				</ul>
			</div>
		</div>
	</div><!-- end nav container -->
</div>

<script>
document.addEventListener('DOMContentLoaded', () => {
	window.TEAK.Modules.megaMenu.init("category_{{parentId}}");
});
</script>

components/header/site-branding:
{{#withLast breadcrumbs}}{{#if name "==" "Home"}}
<h1 class="site-branding {{#if ../../settings.store_logo.image}}has-logo{{/if}}">
{{else}}
<div class="h1 site-branding {{#if ../../settings.store_logo.image}}has-logo{{/if}}">
{{/if}}{{/withLast}}
    <a class="site-branding-link" href="{{urls.home}}">
    {{# if settings.store_logo.image }}
    <span class="site-branding-title sr-only" hidden>{{settings.store_logo.title}}</span>
    <!-- <img src="{{cdn 'assets/img/authenteak-logo.svg?imbypass=on'}}" alt="{{settings.store_logo.title}}"> -->
    <img class="site-branding-logo" src="{{getImage settings.store_logo.image 'logo'}}" alt="{{settings.store_logo.title}}">
    {{else}}
      <span class="site-branding-title">{{settings.store_logo.title}}</span>
    {{/if}}
  </a>
{{#withLast breadcrumbs}}{{#if name "==" "Home"}}
</h1>
{{else}}
</div>
{{/if}}{{/withLast}}
