components/common/share-buttons:
{{#if share_buttons}}
  <nav class="share-buttons-nav">
    <ul class="share-buttons" data-share-buttons>

      {{#each share_buttons.buttons}}
        {{#if service '===' 'facebook'}}
          <li class="social-link facebook">
            <a target="_blank" href="http://www.facebook.com/sharer.php?u={{../../url}}" class="facebook">
              {{> svg/social/facebook}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'twitter'}}
          <li class="social-link twitter">
            <a target="_blank" href="https://twitter.com/share?url={{../../url}}&amp;text={{../../title}}" class="twitter">
              {{> svg/social/twitter}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'google_plusone'}}
          <li class="social-link google-plus">
            <a target="_blank" href="https://plus.google.com/share?url={{../../url}}" class="google">
              {{> svg/social/googleplus}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'linkedin'}}
          <li class="social-link linkedin">
            <a target="_blank" href="http://www.linkedin.com/shareArticle?url={{../../url}}&amp;title={{../../title}}" class="linkedin">
              {{> svg/social/linkedin}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'email'}}
          <li class="social-link mail">
            <a href="mailto:?subject={{../../title}}&amp;body={{../../url}}" class="email">
              {{> svg/social/email}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'print'}}
          <li class="social-link print">
            <a class="print" href="javascript:;">
              {{> svg/social/print}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'pinterest'}}
          <li class="social-link pinterest">
            <a target="_blank" href="//pinterest.com/pin/create/button/?url={{../../url}}&amp;media={{../../image}}&amp;description={{../../title}}" class="share-pinterest">
              {{> svg/social/pinterest}}
            </a>
          </li>
        {{/if}}
      {{/each}}

      {{#each share_buttons.buttons}}
        {{#if service '===' 'facebook_like'}}
          <li class="social-link facebook-like">
            <div class="fb-like"
              data-href="{{../../url}}"
              data-layout="button_count"
              data-action="like"
              data-show-faces="false">
            </div>
          </li>
        {{/if}}
      {{/each}}

    </ul>
  </nav>
{{/if}}

components/products/bulk-pricing:
{{#if product.bulk_discount_rates.length}}
  <div class="bulk-pricing-block">
    <h1 class="product-details-heading">{{lang 'product.bulk_pricing.title'}}</h1>
    <ul>
      {{#each product.bulk_discount_rates}}
        <li>
          {{lang 'product.bulk_pricing.range' min=min max=max}}
          {{#if type '===' 'percent'}}
            {{lang 'product.bulk_pricing.percent' discount=discount.formatted}}
          {{/if}}
          {{#if type '===' 'fixed'}}
            {{lang 'product.bulk_pricing.fixed' discount=discount.formatted}}
          {{/if}}
          {{#if type '===' 'price'}}
            {{lang 'product.bulk_pricing.price' discount=discount.formatted}}
          {{/if}}
        </li>
      {{/each}}
    </ul>
  </div>
{{/if}}

components/products/price:
{{#if price}}
{{#if price.without_tax.value ">" 0.01}}
  {{#or has_options product.options.length}}
    <div>

      {{#if price.without_tax}}
        <div class="product-price-line" data-product-price-wrapper="without-tax" one>
          {{!-- This price is based on the qty count --}}
          <span class="price-value">{{> components/products/customizations/non-sale-price-fixed price=price}}</span>


          {{#if page_type '!==' 'product'}}
            {{#if as_low_as}}
              <span class="as-low-as-pricing">{{lang 'product.as_low_as_price_message'}}</span>
            {{/if}}
          {{/if}}

          {{#if price.saved}}
            <span class="price-rrp {{#unless price.with_tax}}highlight{{/unless}}">{{price.rrp_without_tax.formatted}}</span>
          {{/if}}


          {{!-- <p class="test-bit" data-test-bit="true">---</p>--}}
          {{#if price.with_tax}}
            <span class="tax-label"> {{lang 'product.excluding_tax'}}</span>
          {{/if}}

        </div>
      {{/if}}

      {{#if price.with_tax}}
        <div class="product-price-line{{#if price.without_tax}} price-second-line{{/if}}" data-product-price-wrapper="with-tax">
          <span class="price-value"> {{price.with_tax.formatted}}</span>

          {{#if page_type '!==' 'product'}}
            {{#if as_low_as}}
              <span class="as-low-as-pricing">{{lang 'product.as_low_as_price_message'}}</span>
            {{/if}}
          {{/if}}
          <br>
          {{#if price.saved}}
            <span class="price-rrp {{#unless price.without_tax}}highlight{{/unless}}">{{price.rrp_with_tax.formatted}}</span>
          {{/if}}


          {{#if price.without_tax}}
            <span class="tax-label"> {{lang 'product.including_tax'}}</span>
          {{/if}}
        </div>
      {{/if}}

      {{#if show_savings}}
        {{#if price.saved}}
          <div class="price-you-save" data-product-price-saved>
              {{!-- {{lang 'product.you_save'}} --}} {{price.saved.formatted}}
          </div>
        {{/if}}
      {{/if}}
    </div>


  {{else}}


    <div>
      {{#if price.without_tax}}

        <div class="product-price-line" data-product-price-wrapper="without-tax" two>
          <span class="price-value"> {{> components/products/customizations/non-sale-price-fixed price=price}}</span>

          {{#if price.saved}}
            <span class="price-rrp {{#unless price.with_tax}}highlight{{/unless}}">{{price.rrp_without_tax.formatted}}</span>
          {{/if}}
          <br>

          {{#if price.with_tax}}
            <span class="tax-label"> {{lang 'product.excluding_tax'}}</span>
          {{/if}}
        </div>

      {{/if}}

      {{#if price.with_tax}}

        <div class="product-price-line{{#if price.without_tax}} price-second-line{{/if}}" data-product-price-wrapper="with-tax">
          <span class="price-value"> {{price.with_tax.formatted}}</span>

          {{#if price.saved}}
            <span class="price-rrp {{#unless price.without_tax}}highlight{{/unless}}">{{price.rrp_with_tax.formatted}}</span>
          {{/if}}

          {{#if price.without_tax}}
            <span class="tax-label"> {{lang 'product.including_tax'}}</span>
          {{/if}}
        </div>
      {{/if}}

      {{#if show_savings}}
        {{#if price.saved}}
          <div class="price-you-save" data-product-price-saved>
            {{lang 'product.you_save'}} {{price.saved.formatted}}
          </div>
        {{/if}}
      {{/if}}
    </div>
  {{/or}}
{{/if}}
{{/if}}

components/products/product-description:
{{#if theme_settings.product-single-display-order '===' 'product-description-first'}}
  <div class="product-description">
    {{{product.description}}}

    {{{snippet 'product_description'}}}
  </div>

  <div class="product-stock">
    <span class="{{#unless product.stock_level}}product-details-hidden{{/unless}}" data-product-stock>{{lang 'product.current_stock'}}</span>
    <span class="{{#unless product.stock_level}}product-details-hidden{{/unless}}" data-product-stock data-product-stock-level>{{#if product.stock_level}}{{product.stock_level}}{{else}}0{{/if}}</span>
  </div>
{{else}}
  <div class="product-stock">
    <span class="{{#unless product.stock_level}}product-details-hidden{{/unless}}" data-product-stock>{{lang 'product.current_stock'}}</span>
    <span class="{{#unless product.stock_level}}product-details-hidden{{/unless}}" data-product-stock data-product-stock-level>{{#if product.stock_level}}{{product.stock_level}}{{else}}0{{/if}}</span>
  </div>

  <div class="product-description">
    {{{product.description}}}

    {{{snippet 'product_description'}}}
  </div>
{{/if}}
