core/amp/css/base:
*,
*:after,
*:before {
  box-sizing: border-box;
}

html {
  font-size: 16px;
}

html,
body {
  height: auto;
}

body {
  font-size: 1rem;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
  font-weight: 400;
  line-height: 1.6;
  color: {{color_text}};
  background: {{body_background}};
  padding-top: 55px;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

ul {
  margin: 0;
  padding: 0;
}

li {
  padding: 0;
  margin: 0;
}

a {
  color: {{color_accent}};
  line-height: inherit;
  text-decoration: none;
}

a:hover,
a:focus {
  opacity: 0.7;
}

*:focus {
  outline: 0;
}

p {
  margin-bottom: 1.5rem;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  color: {{color_headings}};
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
  letter-spacing: .25px;
  margin-bottom: .78571rem;
  margin-top: 2.28571rem;
}

h1 {
  font-size: 28px;
}

h2 {
  font-size: 25px
}

h3 {
  font-size: 22px;
}

h4 {
  font-size: 20px;
}

h5 {
  font-size: 15px;
}

h6 {
  font-size: 13px;
  text-transform: uppercase;
}

hr {
  border: solid {{color_borders}};
  border-width: 1px 0 0;
  clear: both;
  height: 0;
  margin: 2rem 0 1.92857rem;
}

ul,
ol,
dl {
  list-style-position: outside;
  margin-bottom: 1.5rem;
}

ul ul,
ul ol,
ol ul,
ol ol {
  margin-left: 1.2rem;
  margin-bottom: 0;
}

ul {
  margin-left: 1.15rem;
}

ol {
  margin-left: 1.15rem;
}

dl dt {
  font-weight: 700;
  margin-bottom: .35714rem;
}

dl dd {
  margin-bottom: 1.5rem;
}

abbr,
acronym {
  border-bottom: 1px dotted {{color_borders}};
  color: {{color_text}};
  cursor: help;
  font-size: 90%;
  text-transform: uppercase;
}

abbr {
  text-transform: none;
}

blockquote,
blockquote p {
  font-size: 22px;
  line-height: 29px;
}

cite {
  color: {{color_accent}};
  display: block;
  font-size: 1rem;
  line-height: 1.5;
}

.body {
  margin-bottom: 6rem;
  margin-top: 1.5rem;
}

.container {
  margin-left: auto;
  margin-right: auto;
  max-width: 91.71429rem;
  padding: 0 1.5rem;
  position: relative;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0,0,0,0);
  border: 0;
}

core/amp/css/header:
.amp-header {
  position: fixed;
  top: 0;
  z-index: 20;
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  height: 55px;
  color: {{header_text}};
  background-color: {{header_background}};
  border-bottom: 1px solid {{color_borders}};
}

.amp-header a {
  color: {{header_text}};
}

.amp-mini-cart,
.amp-header-button {
  z-index: 10;
}

.amp-mini-cart {
  margin-right: 15px;
}

.amp-header-button {
  position: relative;
  display: flex;
  align-self: center;
  justify-content: center;
  flex-shrink: 0;
  width: 18px;
  height: 12px;
  margin-left: 15px;
  cursor: pointer;
  background-color: transparent;
  border: 0;
}

.amp-header-close {
  position: absolute;
  right: 0;
  padding: 10px;
  margin-left: 0;
}

.close,
.burger {
  position: absolute;
  display: block;
  width: 100%;
  height: 1px;
  margin-top: -1px;
}

.burger {
  background-color: {{header_text}};
}

.burger-top {
  top: 0;
}

.burger-bottom {
  top: 100%;
}

.burger-middle {
  top: 50%;
}

.close {
  background-color: {{color_text}};
}

.close-left {
  top: 50%;
  transform: rotate(-45deg);
}

.close-right {
  top: 50%;
  transform: rotate(45deg);
}

.arrow-down {
  position: absolute;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  right: 0px;
	transform: rotate(90deg);
  padding: 4px 8px 8px;
}

.amp-header-link {
  display: inline-block;
  text-decoration: none;
}

.amp-header-logo {
  position: absolute;
  width: 100%;
  text-align: center;
}

.amp-header-logo-container {
  position: relative;
  width: 150px;
  height: 50px;
}

amp-img.amp-header-logo-image img {
  object-fit: contain;
}

.amp-empty-space {
  width: 3.92857rem;
}

.amp-header-logo-text {
  display: block;
  font-family: "Oswald", Arial, Helvetica, sans-serif;
  font-size: 20px;
  font-weight: 300;
  letter-spacing: .35714rem;
  margin-left: auto;
  margin-right: auto;
  overflow: hidden;
  padding: .21429rem 0;
  text-overflow: ellipsis;
  text-transform: uppercase;
  white-space: nowrap;
}

.banners {
  background-color: {{header_background}};
  color: {{header_text}};
  padding: .78571rem 0;
  text-align: center;
}

.banners p:last-child {
  margin-bottom: 0;
}

.banners a {
  color: {{header_text}};
}

.banner {
  margin: 0 auto;
  max-width: 85.71429rem;
  width: 100%;
}

.banner:before,
.banner:after {
  content: " ";
  display: table;
}

.banner:after {
  clear: both;
}

.banner + .banner {
  margin-top: 1.5rem;
}

.form-fieldset {
  border-width: 0;
  margin: 0;
  padding: 0;
}

.amp-navigation-header {
  display: block;
  position: relative;
  margin-bottom: 1rem;
}

.amp-navigation {
  height: 100%;
  min-width: 200px;
  padding: 1rem;
  background-color: {{body_background}};
  overflow-y: auto;
}

.amp-navigation-list {
  margin: 0;
  padding: 0;
  list-style: none;
}

.amp-page-navigation-list {
  margin-top: 1rem;
}

.amp-navigation-list + .amp-navigation-list {
  border-top: 1px solid {{color_borders}};
  padding-top: 1rem;
}

.amp-navigation-list + .amp-navigation-utilities {
  padding-top: 1.25rem;
  margin-top: 1rem;
}

.amp-navigation-link {
  display: block;
  font-size: 15px;
  font-weight: 500;
  color: {{color_text}};
  padding: .78571rem 0;
  text-decoration: none;
}

.amp-navigation-tier-item.has-children > .amp-navigation-link {
  color: {{color_text}};
}

.amp-navigation-link[aria-expanded="true"] .arrow-down {
  transform: rotate(-90deg);
}

.amp-category-navigation-list .amp-navigation-item > .amp-navigation-link {
  color: {{color_text}};
}

.amp-currency-header,
.amp-navigation-link.is-active {
  color: {{color_text}};
}

.amp-navigation-link.is-active {
  color: {{color_text}};
  font-weight: 700;
}

.amp-currency-header,
.parent-link {
  background: transparent;
  border: 0;
}

.amp-currency-dropdown {
  border: 1px solid {{ color_borders }};
  border-radius: 3px;
  padding-left: 12px;
  padding-right: 8px;
  margin-bottom: 4px;
}

.amp-currency-header {
  padding: 0.6rem 0;
}

.amp-currency-header .arrow-down {
  right: 0;
}

.amp-navigation-currency-list {
  margin-left: -12px;
  margin-right: -8px;
}

.amp-navigation-currency-item a {
  display: block;
  padding: 8px 12px;

  display: block}

.amp-navigation-tier-item {
  background: #fff;
}

.amp-navigation-list-tier {
  position: relative;
}

.amp-navigation-list-tier .amp-navigation-tier-item {
  padding-left: 0.75rem;
}

.amp-navigation-tier-item.has-children .amp-navigation-tier-item {
  color: {{color_text}};
  font-weight: 500;
}

.amp-navigation-list-tier .amp-navigation-link.is-active {
  border-top: 0;
  font-weight: 700;
}

.amp-active-currency {
  color: {{color_text}};
  font-weight: 700;
}

.amp-navigation-currency-list {
  list-style: none;
}

.amp-navigation-currency-item a {
  color: {{color_text}};
}

.amp-store-credit {
  text-transform: none;
  padding: 1.25rem 0;
  margin-top: 0.5rem;
  border-top: 1px solid {{color_borders}};
}

.amp-store-credit:hover {
  opacity: 1;
}

.amp-account-link {
  display: inline-block;
}

