core/amp/css/footer:
.amp-footer {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  padding: 0.5rem 1.5rem 2.5rem;
  color: {{footer_text}};
  background: {{footer_background}};
}

.amp-footer a {
  color: {{footer_text}};
}

.amp-footer-section {
  width: 50%;
}

.amp-footer-heading {
  margin-top: 1.5rem;
  font-size: 16px;
  color: {{footer_text}};
}

.amp-footer-list {
  margin: 0;
  padding: 0;
  list-style: none;
}

.amp-footer-list li {
  padding: .25rem 0;
}

.amp-footer-credits {
  width: 100%;
  margin-top: 1.5rem;
  text-align: center;
}

.amp-footer-credits p {
  margin: 0;
}

{{#block 'amp-custom-footer-styles'}}{{/block}}

core/amp/css/category:
.amp-product-catalog-header {
  max-width: 91.71429rem;
  padding: 0.5rem 1.5rem;
  margin-left: auto;
  margin-right: auto;
}

.amp-product-catalog-header p {
  margin: 0;
}

.amp-product-catalog-header a {
  text-decoration: underline;
}

.amp-product-catalog-empty {
  text-align: center;
}

.amp-product-catalog-image {
  padding: 0 0 1rem;
}

.amp-product-catalog-image-title {
  font-size: 20px;
  margin: 7px 0;
  text-align: center;
}

.amp-product-catalog-content {
  margin: 0 1.5rem 3rem;
}

.amp-product-grid {
  display: flex;
  flex-wrap: wrap;
  list-style: none;
  margin: 0;
  padding: 0;
  justify-content: space-between;
}

.amp-product-item {
  width: calc(50% - 10px);
  margin-top: 1.5rem;
}

.columns-one .amp-product-item {
  width: 100%;
}

.alignment-center .amp-product-item {
  text-align: center;
}

.alignment-right .amp-product-item {
  text-align: right;
}

.amp-product-grid-item {
  position: relative;
}

.product-badge {
  position: absolute;
  top: 0;
  left: 0;
  z-index: 1;
  padding: 0.25rem 0.5rem;
  color: #fff;
  background: {{color_accent}};
}

.amp-grid-item-image-container {
  margin: 0 0 15px 0;
  padding: 0;
  width: 100%;
  position: relative;
}

.amp-grid-item-image-container.amp-image-ratio-4-3 {
  padding-bottom: 75%
}

.amp-grid-item-image-container.amp-image-ratio-1-1 {
  padding-bottom: 100%
}

.amp-grid-item-image-container.amp-image-ratio-3-4 {
  padding-bottom: 133.333%
}

amp-img.amp-grid-item-image img {
  object-fit: cover;
}

amp-img.amp-grid-item-image.amp-image-contain img {
  object-fit: contain;
}

.amp-grid-item-image {
  max-width: 100%;
}

.amp-rating-stars {
  margin-top: 12px;
  color: {{color_accent}};
}

.icon-star-full {
  color: inherit;
}

.amp-grid-item-name {
  font-size: 16px;
  padding-right: 1rem;
  margin-top: 0px;
  margin-bottom: 0;
}

.amp-grid-item-name a {
  color: {{color_headings}};
}

.price {
  color: {{color_text}};
  opacity: 0.5;
}

.price-withtax {
  display: block;
}

.price-withouttax {
  display: block;
}

.price-tax-label {
  margin-left: 0.5rem;
}

.price-saved {
  display: block;
  margin-top: 1rem;
  font-size: 1.25rem;
}

.price-value {
  display: inline-block;
}

.price-rrp {
  text-decoration: line-through;
}

.price-is-discounted {
  text-decoration: line-through;
}

.pagination {
  position: relative;
  display: flex;
  justify-content: center;
  margin-top: 2rem;
}

.pagination-item {
  display: inline-flex;
  justify-content: center;
  align-items: center;
  width: 2rem;
  height: 2rem;
  margin-right: 0.5rem;
  margin-left: 0.5rem;
  color: {{color_text}};
  border: 1px solid {{color_borders}};
  transition: all 0.3s ease;
  opacity: 0.5;
}

.pagination-link {
  display: block;
  padding: 3px 10px;
}

.pagination-item.pagination-current {
  border-color: transparent;
  background: {{color_accent}};
  color: #fff;
  opacity: 1;
}

.amp-subcategories-container {
  margin: 0 1.5rem;
  padding: 1.5rem 0;
  border-top: 1px solid {{color_borders}};
}

.amp-subcategories-heading {
  margin-top: 0;
  font-size: 16px;
}

.amp-subcategories-list {
  list-style: none;
  margin: 0;
}

.amp-subcategories-list-item {
  margin: 0;
  padding: 0.25rem 0;
  color: {{color_text}};
}

.amp-subcategories-list-item a {
  color: inherit;
}
