core/amp/css/product:
.amp-product-images {
  margin-top: 30px;
}

.amp-product-images.amp-no-product-images {
  display: inline-block;
  position: relative;
  width: 100%;
  height: 300px;
}

.amp-no-image {
  width: 100%;
  height: 300px;
}

.amp-product-details-container {
  margin: 0 1.5rem 3rem;
}

.amp-product-details {
  margin-top: 20px;
}

.amp-product-brand {
  color: inherit;
}

.amp-product-title {
  font-size: 24px;
  margin-top: 0;
  margin-bottom: 0;
}

.amp-price {
  color: {{color_text}};
  opacity: 0.5;
}

.amp-additional-info {
  margin-top: 20px;
  margin-bottom: 20px;
}

.button {
  padding: 0;
  font-family: inherit;
  background: transparent;
  border: 0;
}

.amp-button-primary {
  position: relative;
  display: inline-block;
  height: 48px;
  width: 100%;
  padding-right: 1.5em;
  padding-left: 1.5em;
  margin-top: 30px;
  overflow: hidden;
  font-size: 16px;
  font-wieght: 700;
  color: #fff;
  line-height: 48px;
  text-align: center;
  transition: all 0.2s ease-out;
  border-radius: 3px;
  background: {{color_accent}};
}

.amp-product-accordion {
  margin-top: 30px;
  border-bottom: 1px solid {{color_border}};
}

.amp-product-accordion-title {
  position: relative;
  font-size: 16px;
  font-wieght: 700;
  color: {{color_headings}};
  padding-top: 20px;
  padding-bottom: 20px;
  border-top: 1px solid {{color_border}};
  border-right: 0;
  border-bottom: 0;
  border-left: 0;
  background: transparent;
}

.amp-product-accordion-title-toggle {
  position: absolute;
  top: 25px;
  right: 15px;
  width: 13px;
  height: 13px;
}

.amp-product-accordion-title-toggle::before,
.amp-product-accordion-title-toggle::after {
  position: absolute;
  display: block;
  background: {{color_text}};
  content: "";
}

.amp-product-accordion-title-toggle::before {
  top: 0;
  left: 5px;
  width: 3px;
  height: 13px;
  transition: all 0.1s ease;
}

.amp-product-accordion-title[aria-expanded="true"] .amp-product-accordion-title-toggle::before {
  display: none;
}

.amp-product-accordion-title-toggle::after {
  top: 5px;
  left: 0;
  width: 13px;
  height: 3px;
}

.amp-product-accordion-content p {
  margin-top: 0;
  color: {{color_text}};
}

.amp-product-video-title {
  margin-top: 15px;
  margin-bottom: 5px;
  font-size: 16px;
  color: {{color_text}};
}

.amp-product-review-list {
  margin: 0;
  list-style: none;
}

.amp-rating-stars {
  color: {{color_accent}};
}

.amp-product-review-title {
  margin-top: 5px;
  font-size: 16px;
  color: {{color_headings}};
}

.amp-product-review-author {
  font-size: 12px;
  text-transform: uppercase;
}

.social-links {
  display: flex;
  flex-wrap: wrap;
  align-items: flex-start;
  margin: 0 0 1.5rem;
}

.social-links-item {
  margin: 0;
  padding: 0;
  display: inline-block;
  font-size: 0;
  margin-bottom: .35714rem;
  margin-right: .78571rem
}

.social-links-item:last-child {
  margin-right: 0
}

{{#block 'amp-custom-product-styles'}}{{/block}}
