core/amp/common/navigation-loop:
{{#if children}}
  <amp-accordion disable-session-states>
    <section>
      <h3 class="amp-navigation-link parent-link {{#if is_active}}is-active{{/if}}">
        {{name}}
        <span class="arrow-down">
          <svg width="9" height="14" viewBox="0 0 7 12" xmlns="http://www.w3.org/2000/svg" role="presentation">
            <path d="M.34 10.66l.71.703L6.364 6.01 1.05.66l-.71.703L4.955 6.01" fill="currentColor"/>
          </svg>
        </span>
      </h3>
      <ul class="amp-navigation-list amp-navigation-list-tier">
        <li class="amp-navigation-tier-item">
          <a class="amp-navigation-link {{#if is_active}}is-active{{/if}}" href="{{url}}">
            {{#if show_all}}
              {{lang 'core.amp.header.all'}} {{name}}
            {{else}}
              {{name}}
            {{/if}}
          </a>
        <li>
        {{#each children}}
          <li class="amp-navigation-tier-item {{#if children}}has-children{{/if}}">
            {{> core/amp/common/navigation-loop
              show_all=../show_all
            }}
          </li>
        {{/each}}
      </ul>
    </section>
  </amp-accordion>
{{else}}
  <a class="amp-navigation-link {{#if is_active}}is-active{{/if}}" href="{{url}}">
    {{name}}
  </a>
{{/if}}

core/amp/common/price:
{{!
  Product Price - Sale vs Normal, With Tax vs Without

  @param this price object

  @param show_savings bool (optional)
         If true, show a label saying how much is saved from the full price.
}}

{{#if price}}
  {{#or has_options product.options.length}}
    <span
      class="price {{> core/amp/common/price-class}}"
      {{#if schema}}
        itemprop="offers"
        itemscope
        itemtype="http://schema.org/Offer"
      {{/if}}
    >
      {{#if price.with_tax}}
        <span class="price-withtax" data-price="with-tax">
          {{#if page_type '!==' 'product'}}
            {{#if as_low_as}}
              <span class="as-low-as-pricing">{{lang 'core.price.as_low_as'}}</span>
            {{/if}}
          {{/if}}
          {{#if price.rrp_with_tax}}
            <span class="price-rrp" data-price-rrp="with-tax">{{price.rrp_with_tax.formatted}}</span>
          {{/if}}
          <span class="price-value" data-price-value="with-tax">
            {{price.with_tax.formatted}}
          </span>
          {{#if price.without_tax}}
            <span class="price-tax-label">{{lang 'core.price.with_tax' tax_label=price.tax_label}}</span>
          {{/if}}
        </span>
      {{/if}}

      {{#if price.without_tax}}
        <span class="price-withouttax" data-price="without-tax">
          {{#if page_type '!==' 'product'}}
            {{#if as_low_as}}
              <span class="as-low-as-pricing">{{lang 'core.price.from'}}</span>
            {{/if}}
          {{/if}}
          {{#if price.rrp_without_tax}}
            <span class="price-rrp" data-price-rrp="without-tax">{{price.rrp_without_tax.formatted}}</span>
          {{/if}}
          <span class="price-value" data-price-value="without-tax">
            {{> components/products/customizations/non-sale-price-fixed price=price}}
          </span>
          {{#if price.with_tax}}
            <span class="price-tax-label">{{lang 'core.price.without_tax' tax_label=price.tax_label}}</span>
          {{/if}}
        </span>
      {{/if}}

      {{#if show_saved}}
        {{#if price.saved}}
          <span class="price-saved" data-price-saved>
            {{lang 'core.price.you_save'}} {{price.saved.formatted}}
          </span>
        {{/if}}
      {{/if}}
    </span>
  {{else}}
    <span
      class="price {{> core/amp/common/price-class}}"
      {{#if schema}}
        itemprop="offers"
        itemscope
        itemtype="http://schema.org/Offer"
      {{/if}}
    >
      {{#if price.with_tax}}
        <span class="price-withtax" data-price="with-tax">
          {{#if price.rrp_with_tax}}
            <span class="price-rrp" data-price-rrp="with-tax">{{price.rrp_with_tax.formatted}}</span>
          {{/if}}
          <span class="price-value" data-price-value="with-tax">
            {{price.with_tax.formatted}}
          </span>
          {{#if price.without_tax}}
            <span class="price-tax-label">{{lang 'core.price.with_tax' tax_label=price.tax_label}}</span>
          {{/if}}
        </span>
      {{/if}}

      {{#if price.without_tax}}
        <span class="price-withouttax" data-price="without-tax">
          {{#if price.rrp_without_tax}}
            <span class="price-rrp" data-price-rrp="without-tax">{{price.rrp_without_tax.formatted}}</span>
          {{/if}}
          <span class="price-value" data-price-value="without-tax">
            {{> components/products/customizations/non-sale-price-fixed price=price}}
          </span>
          {{#if price.with_tax}}
            <span class="price-tax-label">{{lang 'core.price.without_tax' tax_label=price.tax_label}}</span>
          {{/if}}
        </span>
      {{/if}}

      {{#if show_saved}}
        {{#if price.saved}}
          <span class="price-saved" data-price-saved>
            {{lang 'core.price.you_save' amount=price.saved.formatted}}
          </span>
        {{/if}}
      {{/if}}
    </span>
  {{/or}}
{{/if}}

core/amp/common/price-class:
{{#if price.with_tax}}
  {{#unless price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-taxed-onsale
    {{else}}
      prices-taxed
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.without_tax}}
  {{#unless price.with_tax}}
    {{#if price.rrp_without_tax}}
      prices-notax-onsale
    {{else}}
      prices-notax
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.with_tax}}
  {{#if price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-both-onsale
    {{else}}
      prices-both
    {{/if}}
  {{/if}}
{{/if}}

core/amp/common/pagination:
{{!--
  Pagination: Previous / Next buttons and a list of links to other pages.
  @param this Object containing links, count, etc
--}}

{{#any previous links next}}
  <nav class="pagination">
    {{#if previous}}
      <a
        class="pagination-item pagination-previous"
        href="{{previous}}"
        {{#unless reload}}
          data-faceted-search-facet
        {{/unless}}
      >
        <svg width="7" height="12" viewBox="0 0 7 12" xmlns="http://www.w3.org/2000/svg" role="presentation">
          <path d="M6.01 10.648l-.71.704L-.01 6 5.3.648l.71.704L1.4 6" fill="currentColor"/>
        </svg>
        <span class="sr-only">
          {{lang 'core.pagination.previous'}}
        </span>
      </a>
    {{/if}}

    {{#each links}}
      <a
        class="
          pagination-item
          {{#if number '==' ../current}}
            pagination-current
          {{/if}}
        "
        href="{{url}}"
        {{#unless reload}}
          data-faceted-search-facet
        {{/unless}}
      >
        {{number}}
      </a>
    {{/each}}

    {{#if next}}
      <a
        class="pagination-item pagination-next"
        href="{{next}}"
        {{#unless reload}}
          data-faceted-search-facet
        {{/unless}}
      >
        <svg width="7" height="12" viewBox="0 0 7 12" xmlns="http://www.w3.org/2000/svg" role="presentation">
          <path d="M.34 10.66l.71.703L6.364 6.01 1.05.66l-.71.703L4.955 6.01" fill="currentColor"/>
        </svg>
        <span class="sr-only">
          {{lang 'core.pagination.next'}}
        </span>
      </a>
    {{/if}}
  </nav>
{{/any}}
