components/header/customizations/site-na:
{{> components/header/customizations/site-navigation-static}}{{!--
<nav class="navigation" itemscope itemtype="http://www.schema.org/SiteNavigationElement">
  <ul>
    {{#if theme_settings.global-show-home-in-nav}}
      <li><a class="top-level-nav-link" href="{{urls.home}}"><span itemprop="name">{{lang 'header.home_nav_item'}}</span></a></li>
    {{/if}}
    {{#if theme_settings.global-show-categories-in-nav}}
      {{#each categories}}
        {{#startsWith "_" name}}{{ else }}
        {{#if children}}
          <li class="hover-dropdown mega-nav">
            <a class="top-level-nav-link" itemprop="url" href="{{url}}">
              <span itemprop="name">{{name}}</span>
              <!--{{> svg/arrow-down}}-->
            </a>
            {{> components/common/customizations/navigation-mega nav_items=children}}
          </li>
        {{else}}
          <li>
            <a class="top-level-nav-link" itemprop="url" href="{{url}}"><span itemprop="name">{{name}}</span></a>
          </li>
        {{/if}}
        {{/startsWith}}
      {{/each}}
    {{/if}}
    {{#if theme_settings.global-header-logo-alignment '===' 'center'}}
      {{#if show_search}}
        <li>
          <button class="button-search-toggle">
            {{> svg/search}}
          </button>
        </li>
      {{/if}}
    {{/if}}
  </ul>

  {{#if show_search}}
    <button class="button-search-toggle">
      {{> svg/search}}
    </button>
  {{/if}}

</nav>
--}}

components/common/customizations/navigat:
{{!
  @param nav_items
         > either "pages" or "categories", passed from parent template
}}

<div class="dropdown-panel mega-nav-panel">
<div class="container">
<div class="mega-nav-landing-wrapper">

<div class="mega-nav-landing-list-wrapper">
<ul class="mega-nav-list {{#if theme_settings.wrap-meganav}}wrap-cols{{/if}}">

{{#each nav_items}}
  {{#startsWith "_" name}}{{ else }}
  <li class="parent {{#if children}}has-children tier-dropdown{{/if}}">
    <a itemprop="url" href="{{url}}"{{#if image}}data-category-image="{{getImage image "large"}}"{{/if}}><span itemprop="name">{{name}}</span></a>
    {{#if children}}
      <ul class="children">
        {{#each children}}
        {{#startsWith "_" name}}{{ else }}
          <li>
            <a itemprop="url" href="{{url}}"{{#if image}}data-category-image="{{getImage image "large"}}"{{/if}}><span itemprop="name">{{name}}</span></a>
            {{#if children}}
              <ul>
                {{#each children}}
                  {{> components/common/customizations/site-navigation-submenus}}
                {{/each}}
              </ul>
            {{/if}}
          </li>
        {{/startsWith}}
        {{/each}}
      </ul>
    {{/if}}
  </li>
  {{/startsWith}}
{{/each}}
</ul></div>

</div>
</div>

components/common/customizations/site-na:
{{#startsWith "_" name}}{{ else }}
<li {{#if children}}class="tier-dropdown"{{/if}}>
  <a {{#if children}}class="tier-toggle"{{/if}} itemprop="url" href="{{url}}"{{#if image}}data-category-image="{{getImage image "large"}}"{{/if}}><span itemprop="name">{{name}}</span></a>
  {{#if children}}
    <ul class="tier-panel">
      <li class="parent-all"><a href="{{url}}"{{#if image}}data-category-image="{{getImage image "large"}}"{{/if}}>{{lang 'common.navigation_all'}} {{name}}</a></li>
      {{#each children}}
        {{> components/common/customizations/site-navigation-submenus}}
      {{/each}}
    </ul>
  {{/if}}
</li>
{{/startsWith}}
