core/amp/css/footer:
.amp-footer {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  padding: 0.5rem 1.5rem 2.5rem;
  color: {{footer_text}};
  background: {{footer_background}};
}

.amp-footer a {
  color: {{footer_text}};
}

.amp-footer-section {
  width: 50%;
}

.amp-footer-heading {
  margin-top: 1.5rem;
  font-size: 16px;
  color: {{footer_text}};
}

.amp-footer-list {
  margin: 0;
  padding: 0;
  list-style: none;
}

.amp-footer-list li {
  padding: .25rem 0;
}

.amp-footer-credits {
  width: 100%;
  margin-top: 1.5rem;
  text-align: center;
}

.amp-footer-credits p {
  margin: 0;
}

{{#block 'amp-custom-footer-styles'}}{{/block}}
